import React from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from 'mapbox-gl/dist/mapbox-gl-csp';
import MapboxWorker from 'worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker';

mapboxgl.workerClass = MapboxWorker;
mapboxgl.accessToken = 'pk.eyJ1IjoidHJtYXR0ayIsImEiOiJja210dGd2cjYwdXRzMnhzOTN1MDRsYnI2In0.OdQglh6qpMq-QQOm12p8Ng';

//class Notification extends React.Component {
//  // class for proximity notification
//}

class Technician extends React.Component {
  // class for solar panel technician
}

class Map extends React.Component {
  // class for the map
  constructor(props) {
    super(props);
    this.state = {
      lng: -115.61,
      lat: 32.67,
      zoom: 12
    };
    this.mapContainer = React.createRef();
  }

  componentDidMount() {
    const {lng, lat, zoom} = this.state;
    const map = new mapboxgl.Map({
      container: this.mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });
    var marker = new mapboxgl.Marker({
      rotation: 180 // bearing
    })
      .setLngLat([lng, lat]) // position
      .setPopup(new mapboxgl.Popup({
        offset: 20
      }))
      .setHTML('<h5>' + 'Technician' + '</h5>')
      .addTo(map);
    map.on('move', () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      });
    });
  }

  render() {
    const {lng, lat, zoom} = this.state;
    return (
      <div>
        <div className="sidebar">
          Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
        </div>
        <div ref={this.mapContainer} className="map-container" />
        </div>
    );
  }
}

//class App extends React.Component {
//  // class for desktop app
//}

// ===========================================

ReactDOM.render(
  <Map />,
  document.getElementById('app')
);
